CREATE TABLE Division(  Division_name char(225) NOT NULL,  CONSTRAINT Division_name PRIMARY KEY (Division_name));CREATE TABLE NGO(  name char(225),  email char(225) NOT NULL,  date_joined datetime,  phone char(225),  address char(225),  hospital char(225),  division char(225),  bio char(225),  CONSTRAINT email PRIMARY KEY (email));CREATE TABLE Request(  Name char(225),  Email char(225) NOT NULL,  phone char(225),  address char(225),  CONSTRAINT PK_email PRIMARY KEY (Email));CREATE TABLE User(  User_email char(225) NOT NULL,  User_password char(225),  User_name char(225) NOT NULL,  CONSTRAINT user_id PRIMARY KEY (User_email));ALTER TABLE Division ADD CONSTRAINT FK_Division_  FOREIGN KEY (Division_name) REFERENCES NGO (email) ON DELETE NO ACTION;ALTER TABLE NGO ADD CONSTRAINT FK_NGO_  FOREIGN KEY (email) REFERENCES User (User_email) ON DELETE CASCADE ON UPDATE NO ACTION;ALTER TABLE Request ADD CONSTRAINT FK_Request_  FOREIGN KEY (Email) REFERENCES NGO (email) ON DELETE CASCADE ON UPDATE NO ACTION;